package jo.cephus.core.data;import java.util.List;import jo.audio.util.*;import org.json.simple.JSONObject;public class ShipDesignBean    implements IJSONAble{    public ShipDesignBean()    {    }    public ShipDesignBean(JSONObject json)    {        fromJSON(json);    }    public JSONObject toJSON()    {        return ToJSONLogic.toJSONFromBean(this);    }    public void fromJSON(JSONObject o)    {        FromJSONLogic.fromJSON(this, o);    }    public String getShipID()    {        return mShipID;    }    public void setShipID(String shipID)    {        mShipID = shipID;    }    public String getShipName()    {        return mShipName;    }    public void setShipName(String shipName)    {        mShipName = shipName;    }    public String getShipFunction()    {        return mShipFunction;    }    public void setShipFunction(String shipFunction)    {        mShipFunction = shipFunction;    }    public List<ShipComponentInstanceBean> getComponents()    {        return mComponents;    }    public void setComponents(List<ShipComponentInstanceBean> components)    {        mComponents = components;    }    private String mShipID;    private String mShipName;    private String mShipFunction;    private List<ShipComponentInstanceBean> mComponents;}